using System;
using Zafiro.DivineBytes;

namespace DotnetPackaging.Dmg;

internal static class DefaultDmgLayout
{
    private static readonly Lazy<byte[]> DsStoreBytes = new(() => Convert.FromBase64String(DefaultDsStoreBase64));
    private static readonly Lazy<byte[]> BackgroundBytes = new(() => Convert.FromBase64String(DefaultBackgroundBase64));

    public static IByteSource DsStore => ByteSource.FromBytes(DsStoreBytes.Value);
    public static IByteSource BackgroundPng => ByteSource.FromBytes(BackgroundBytes.Value);

    // Generated on macOS with Finder to set icon view, background image, and positions for App.app and Applications
    private const string DefaultDsStoreBase64 = "";

    // Simple light-gray background with a hint of texture; 225x225 PNG
    private const string DefaultBackgroundBase64 = "";
}
